import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { PayPalButton } from 'react-paypal-button-v2'
import { Button, Row, Col, ListGroup, Image, Card } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import Message from '../components/Message'
import Loader from '../components/Loader'
import { getOrderDetails, payOrder, deliverOrder } from '../actions/orderActions'
import { ORDER_PAY_RESET, ORDER_DELIVER_RESET } from '../constants/orderConstants'

const OrderScreen = ({ match, history }) => {
    const orderId = match.params.id     

    const [sdkReady, setSdkReady] = useState(false)

    const dispatch = useDispatch()

    const orderDetails = useSelector((state) => state.orderDetails)
    const { order, loading, error } = orderDetails

    const orderPay = useSelector((state) => state.orderPay)
    const { loading: loadingPay, success: successPay } = orderPay 

    const orderDeliver = useSelector((state) => state.orderDeliver)
    const { loading: loadingDeliver, success: successDeliver } = orderDeliver

    const userLogin = useSelector((state) => state.userLogin)
    const { userInfo } = userLogin

    if (!loading) {
        
        const addDecimals = (num) => {
            return (Math.round(num * 100) / 100).toFixed(2)
        }

        
        order.itemsPrice = addDecimals(order.orderItems.reduce(
            (acc, item) => acc + item.price * item.qty, 0))
    }

    useEffect(() => {

        if(!userInfo){  
            history.push('/login')
        }

        
        const addPayPalScript = async () => {
            const { data: clientId } = await axios.get('/api/config/paypal')
            const script = document.createElement('script')
            script.type = 'text/javascript'
            script.src = `https://www.paypal.com/sdk/js?client-id=${clientId}`
            script.async = true
            script.onload = () => { setSdkReady(true) }
            document.body.appendChild(script)
        }

        if (!order || order._id !== orderId || successPay || successDeliver) {
            dispatch({ type: ORDER_PAY_RESET })
            dispatch({ type: ORDER_DELIVER_RESET })

            dispatch(getOrderDetails(orderId))
        } else if (!order.isPaid) {
            if (!window.paypal) {
                addPayPalScript()
            } else {
                setSdkReady(true)
            }
        }
    }, [order, orderId, successPay, successDeliver])

    const successPaymentHandler = (paymentResult) => {
        
        console.log(paymentResult)
        dispatch(payOrder(orderId, paymentResult))
    }

    const deliverHandler = () => {
        dispatch(deliverOrder(order))
    }

    return loading ? <Loader />
        : error ? <Message variant='danger'>{error}</Message>
            : <>
                <h1>Sipariş {order._id}</h1>
                <Row>
                    <Col md={8}>
                        <ListGroup variant='flush'>
                            <ListGroup.Item>
                                <h2>Gönderim</h2>
                                <p><strong>İsim Soyisim: </strong>{order.user.name} </p>
                                <p><strong>Email: </strong><a href={`mailto:${order.user.email}`}> {order.user.email} </a></p>
                                <p><strong>Adres: </strong>
                                    {order.shippingAddress.address}, {order.shippingAddress.city},
                            {' '}{order.shippingAddress.postalCode}, {order.shippingAddress.country}</p>
                                {order.isDelivered ? <Message variant='success'>Teslim Edildi: {order.deliveredAt}</Message>
                                    : <Message variant='danger'>Teslim Edilmedi</Message>}
                            </ListGroup.Item>

                            <ListGroup.Item>
                                <h2>Ödeme Yöntemi</h2>
                                <p><strong>Ödeme Yöntemi: </strong>
                                    {order.paymentMethod}</p>
                                {order.isPaid ? <Message variant='success'>Ödendi: {order.paidAt}</Message>
                                    : <Message variant='danger'>Ödenmedi</Message>}
                            </ListGroup.Item>

                            <ListGroup.Item>
                                <h2>Sipraiş Edilen Ürünler</h2>
                                {order.orderItems.length === 0
                                    ? <Message>Sipariş Edilen Ürün Yok</Message>
                                    : (<ListGroup variant='flush'>
                                        {order.orderItems.map((item, index) => (
                                            <ListGroup.Item key={index}>
                                                <Row>
                                                    <Col md={1}>
                                                        <Image src={item.image} alt={item.name} fluid rounded />
                                                    </Col>
                                                    <Col>
                                                        <Link to={`/product/${item.product}`}>{item.name}</Link>
                                                    </Col>
                                                    <Col md={4}>
                                                        {item.qty} x ₺{item.price} = ₺{item.qty * item.price}
                                                    </Col>
                                                </Row>
                                            </ListGroup.Item>
                                        ))}
                                    </ListGroup>
                                    )}
                            </ListGroup.Item>
                        </ListGroup>
                    </Col>
                    <Col md={4}>
                        <Card>
                            <ListGroup variant='flush'>
                                <ListGroup.Item>
                                    <h2>Sipariş Geçmişi</h2>
                                </ListGroup.Item>
                                <ListGroup.Item>
                                    <Row>
                                        <Col>Ürünler</Col>
                                        <Col>₺{order.itemsPrice}</Col>
                                    </Row>
                                </ListGroup.Item>
                                <ListGroup.Item>
                                    <Row>
                                        <Col>Gönderim</Col>
                                        <Col>₺{order.shippingPrice}</Col>
                                    </Row>
                                </ListGroup.Item>
                                <ListGroup.Item>
                                    <Row>
                                        <Col>Vergi</Col>
                                        <Col>₺{order.taxPrice}</Col>
                                    </Row>
                                </ListGroup.Item>
                                <ListGroup.Item>
                                    <Row>
                                        <Col>Toplam</Col>
                                        <Col>₺{order.totalPrice}</Col>
                                    </Row>
                                </ListGroup.Item>
                                {!order.isPaid && (
                                    <ListGroup.Item>
                                        {loadingPay && <Loader />}
                                        {!sdkReady ? <Loader /> : (
                                            <PayPalButton amount={order.totalPrice} onSuccess={successPaymentHandler} />
                                        )}
                                    </ListGroup.Item>
                                )}
                                {loadingDeliver && <Loader />}
                                {userInfo && userInfo.isAdmin && order.isPaid && !order.isDelivered && (
                                    <ListGroup.Item>
                                        <Button type='button' className='btn btn-block' onClick={deliverHandler}>
                                            Teslim Edildi olarak işaretle
                                        </Button>
                                    </ListGroup.Item>
                                )}
                            </ListGroup>
                        </Card>
                    </Col>
                </Row>
            </>
}

export default OrderScreen
